mapper.select:
select seq, id, title, content, regdate, readcount
from answerboard
where seq=#{seq}

mapper.update:
UPDATE answerboard
SET readcount=readcount+1
WHERE seq=#{seq}

UPDATE answerboard
SET title=#{title}, content=#{content}, regdate=SYSDATE()
WHERE seq = #{seq}

mapper.select:
SELECT rn, seq, id, title, content, regdate,refer,step,depth, readcount,delflag FROM ( SELECT ROW_NUMBER() OVER(ORDER BY refer DESC,step) rn, seq, id, title, content, regdate,refer,step,depth, readcount,delflag FROM answerboard) a
seq=#{seq}
CEIL(rn/10)=#{pnum}
