mapper.select:
    SELECT seq, id, title, content,mdate,regdate
	FROM calboard
	WHERE SUBSTRING(mdate,1,8) = #{yyyyMMdd}
	AND id=#{id}

SELECT seq,id,title,content,mdate,regdate
FROM calboard
WHERE seq=#{seq}

SELECT rn,seq, id,title,content, mdate, regdate	
from	(SELECT ROW_NUMBER() over(PARTITION BY SUBSTR(mdate,1,8)
	                             ORDER BY mdate) rn
	       , seq, id,title,content, mdate, regdate
	    FROM calboard
		 WHERE id=#{id} AND SUBSTR(mdate,1,6)=#{yyyyMM}
		 ) cal
WHERE rn BETWEEN 1 AND 3
